static int yy_init_globals (void)
{
        /* Initialization is the same as for the non-reentrant scanner.
     * This function is called from testlex_destroy(), so don't allocate here.
     */

    (yy_buffer_stack) = 0;
    (yy_buffer_stack_top) = 0;
    (yy_buffer_stack_max) = 0;
    (yy_c_buf_p) = (char *) 0;
    (yy_init) = 0;
    (yy_start) = 0;

    (yy_state_buf) = 0;
    (yy_state_ptr) = 0;
    (yy_full_match) = 0;
    (yy_lp) = 0;

/* Defined in main.c */
#ifdef YY_STDINIT
    testin = stdin;
    testout = stdout;
#else
    testin = (FILE *) 0;
    testout = (FILE *) 0;
#endif

    /* For future reference: Set errno on error, since we are called by
     * testlex_init()
     */
    return 0;
}

/* testlex_destroy is for both reentrant and non-reentrant scanners. */
int testlex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		test_delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		testpop_buffer_state();
	}

	/* Destroy the stack itself. */
	testfree((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    testfree ( (yy_state_buf) );
    (yy_state_buf)  = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * testlex() is called, initialization will occur. */
    yy_init_globals( );

    return 0;
}

